INSERT INTO `tbl_education_saving_plan` (`bank_id`, `installment`, `status`, `year`, `interest`, `deposit_amount`, `amount_with_interest`, `description`, `about_education_saving_plan`) VALUES
(30, 5000, 'available', 10, 8, 600000, 1080000, 'ক) &nbsp;&nbsp; &nbsp;হিসাবের মেয়াদকাল &nbsp;&nbsp; &nbsp;: &nbsp;&nbsp; &nbsp;১০(দশ বৎসর)<br>খ) &nbsp;&nbsp; &nbsp;মাসিক কিস্তির পরিমাণ &nbsp;&nbsp; &nbsp;: &nbsp;&nbsp; &nbsp;৫০০ টাকা, কিংবা ইহার গুনীতক সর্বোচ্চ ১০,০০০ টাকা<br>গ) &nbsp;&nbsp; &nbsp;সুদের হার &nbsp;&nbsp; &nbsp;: &nbsp;&nbsp; &nbsp;বার্ষিক ৮.০০ শতাংশ ( চক্রবৃদ্ধি হারে)<br>ঘ) &nbsp;&nbsp; &nbsp;কিস্তি প্রদানের তারিখ &nbsp;&nbsp; &nbsp;: &nbsp;&nbsp; &nbsp;প্রতি মাসের ১০ হইতে ১৫ তারিখের মধ্যে। অগ্রিম কিস্তি জমা দেবার সুবিধা আছে।<br>ঙ) &nbsp;&nbsp; &nbsp;হিসাব খোলার নিয়মাবলীঃ &nbsp;&nbsp; &nbsp;: &nbsp;&nbsp; &nbsp;-১৮বা তদূর্ধ্ব বয়সের সুস্থ&nbsp; যে কোন বাংলাদেশী নাগরিক এই হিসাব খুলতে পারবে।<br>- হিসাব খোলার সময় গ্রাহকের সম্প্রতি তোলা পাসপোর্ট সাইজের ২কপি সত্যায়িত ছবি লাগবে।<br>- নমিনী বাধ্যতামূলক।<br>চ) &nbsp;&nbsp; &nbsp;কিস্তি খেলাপী হলেঃ &nbsp;&nbsp; &nbsp;: &nbsp;&nbsp; &nbsp;নির্দিষ্ট তারিখের মধ্যে মাসিক কিস্তি জমা দানে ব্যর্থ হলে নির্ধারিত ফিসহ পরবর্তী মাসের কিস্তির সঙ্গে খেলাপী কিস্তি জমা দেয়া যাবে।<br>ছ) &nbsp;&nbsp; &nbsp;পর পর ২(দুই) মাসের কিস্তি&nbsp; খেলাপী হলে হিসাবটি &nbsp;&nbsp; &nbsp;: &nbsp;&nbsp; &nbsp;প্রতি ১০০.০০ টাকা কিস্তির প্রতি মাসে সুদ ১.০০ টাকা এবং পুনঃচালুকরণ ফি বাবদ প্রতি বারের জন্য প্রতি ১০০.০০ টাকায় ৫.০০ টাকা এবং ৬মাসের বেশী খেলাপী হলে জরিমানার অতিরিক্ত হিসেবে প্রতি হিসাবে ১০০.০০ টাকা আদায়পূর্বক ব্যবস্থাপকের অনুমোদনসাপেক্ষে হিসাবটি পুনরায় চালু করা যাবে।<br>জ) &nbsp;&nbsp; &nbsp;মেয়াদপূর্তির পূর্বে হিসাব বন্ধ করা হলে &nbsp;&nbsp; &nbsp;: &nbsp;&nbsp; &nbsp;-হিসাব খোলার ১(এক) বছর পর্যন্ত সময়ের মধ্যে হিসাব বন্ধ করা হলে শুধুমাত্র জমাকৃত মূল টাকা ফেরত দেয়া হবে।<br>- হিসাবের মেয়াদ ১(এক) বছরের অধিক কিন্তু ৩(তিন) বছর পর্যন্ত ৫% হারে সরল সুদসহ জমাকৃত টাকা ফেরত দেয়া হবে।<br>- হিসাবের মেয়াদ ৩(তিন) বছরের অধিক কিন্তু ৫ বছরের কম হলে ৬% হারে সরল সুদসহ জমাকৃত টাকা ফেরত দেয়া হবে।<br>- হিসাবের মেয়াদ ৫(পাঁচ) বছরের অধিক কিন্তু ১০(দশ) বছরের কম হলে ৭% হারে সরল সুদসহ জমাকৃত টাকা ফেরত দেয়া হবে।<br>ঝ) &nbsp;&nbsp; &nbsp;ঋণসুবিধা &nbsp;&nbsp; &nbsp;: &nbsp;&nbsp; &nbsp;তাৎক্ষণিক নগদায়ন মূল্যের সর্বোচ্চ ৯০% । সুদের হার আমানত হিসাবে প্রদত্ত সুদ হারের ২% উর্দ্ধে।<br>ঞ) &nbsp;&nbsp; &nbsp;বোনাস সুবিধাঃ &nbsp;&nbsp; &nbsp;: &nbsp;&nbsp; &nbsp;হিসাব খোলার পর থেকে কোন কিস্তি খেলাপী না হলে মেয়াদ পূর্তী শেষে নির্ধারিত হারে বোনাস প্রদান করা হবে।', ''),
