INSERT INTO `tbl_islami_deposit` (`bank_id`, `description`, `website`) VALUES
(24, '<font face="Arial" size="3"><b><font size="5" color="#ff0000">Mudaraba Term Deposit Account (MTDR):</font></b><br><br><font color="#000099"><b><font size="4">Main Features:</font></b></font><br></font><ul><li><font face="Arial" size="3">Mudaraba Term Deposit Receipt (MTDR) is a profit bearing account which offers returns by depositing money for fixed period of time based on Mudaraba concept. These deposits are repayable subject to a period of notice and hence known as time deposits or time liabilities meaning thereby that these are withdrawable subject to a period of notice and not on demand. Tk.1000/- and multiple of Tk.100/- for the period of 3 months, 6 months, 12 months, 24 months and 36 months can be deposited.</font></li></ul><font face="Arial" size="3"><br><font size="4" color="#000099"><b>Required Documents:</b></font><br></font><ul><li><font face="Arial" size="3">Complete account opening form</font></li><li><font face="Arial" size="3">02 copies passport size photographs of account holder duly attested by introducer</font></li><li><font face="Arial" size="3">1 copy photograph of nominee duly attested by the accountholder</font></li><li><font face="Arial" size="3">Identification proof like National ID Card/Passport/Chairman certificate</font></li><li><font face="Arial" size="3">Minimum deposit Tk.1000/-</font></li><li><font face="Arial" size="3">Introducer signature.</font></li></ul><font face="Arial" size="3"><br></font>', 'http://www.islamibankbd.com/prodServices/ds_detail.php?ds_id=3'),
(7, '<font face="Arial" size="3"><font size="5" color="#ff0000"><b>Manarah Term Deposit:</b></font><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Manarah Term deposit is for long term deposit product under Mudarabah principles with higher rate of return.<br><br><font size="4" color="#000099"><b>Product Features:</b></font><br></font><ul><li><font face="Arial" size="3">Deposit amount minimum BDT 50,000</font></li><li><font face="Arial" size="3">Deposit tenor 1 month, 3 months, 6 months, 1 year, 2 years and 3 years</font></li><li><font face="Arial" size="3">Premature encasement allowed subject to banks prevailing policy</font></li><li><font face="Arial" size="3">Can be availed by any Bangladeshi national aged 18 years.</font></li></ul><font face="Arial" size="3"><b><br><font color="#330099">Note:</font></b><font color="#330099"> <i>For provisional rate please call or visit your nearest City Bank Branch.</i></font></font>', 'https://www.thecitybank.com/Manarah-Term-Deposit-Receipt.php'),
(21, '<font face="Arial" size="3"><font size="5" color="#ff0000"><b>Mudaraba Term Deposit Receipt:</b></font><br>&nbsp; &nbsp; &nbsp; <br>&nbsp;Mudaraba Term Deposit Receipt (MTDR) is a profit bearing account which offers returns by depositing money for fixed period of time based on Mudaraba concept. These deposits are repayable subject to a period of notice and hence known as time deposits or time liabilities meaning thereby that these are withdrawable subject to a period of notice and not on demand.<br><br><font size="4" color="#000099"><b>Different Tenor of MTDR Products:</b></font><br></font><ul><li><font face="Arial" size="3">Mudaraba Term Deposit – 1 month</font></li><li><font face="Arial" size="3">Mudaraba Term Deposit – 3 months</font></li><li><font face="Arial" size="3">Mudaraba Term Deposit – 6 months</font></li><li><font face="Arial" size="3">Mudaraba Term Deposit – 12 months</font></li><li><font face="Arial" size="3">Mudaraba Term Deposit – 24 months</font></li><li><font face="Arial" size="3">Mudaraba Term Deposit – 36 months or above</font></li></ul><font face="Arial" size="3"><br><font size="4" color="#000099"><b>Key Features:</b></font><br></font><ul><li><font face="Arial" size="3">It is a profit and loss sharing product.</font></li><li><font face="Arial" size="3">Term Deposit Receipts are issued against deposits</font></li><li><font face="Arial" size="3">Minimum account opening deposit is Tk. 1,000/- and above, upto any amount in multiples of Tk. 1000/-</font></li><li><font face="Arial" size="3">Profit is calculated on daily basis and credited on half yearly basis using the provisional rate of profit.</font></li><li><font face="Arial" size="3">Investment can be availed against MTDR.</font></li></ul>', 'https://www.primebank.com.bd/index.php/home/mudaraba_term_deposit_receipt'),
(25, '<font face="Arial" size="3"><font size="5" color="#ff0000"><b>Mudaraba Term Deposit Receipt:</b></font><br><br><font size="4" color="#000099"><b>Terms and Conditions:</b></font><br></font><ul><li><font face="Arial" size="3">Specific papers/ documents on the basis of the type of the customer.</font></li><li><font face="Arial" size="3">Photograph of Account holder is required.</font></li><li><font face="Arial" size="3">Specimen Signature.</font></li><li><font face="Arial" size="3">Nominee required.</font></li></ul><font face="Arial" size="3"><br><font size="4" color="#000099"><b>Eligibility:</b></font><br></font><ul><li><font face="Arial" size="3">Individual (Adult).</font></li></ul><font face="Arial" size="3"><br><font size="4" color="#000099"><b>Benefits/Features:</b></font><br><br><font color="#330099"><b>Mudaraba Term Deposit &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Investment Income Sharing Ratio<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Bank &nbsp;&nbsp; &nbsp;Depositor</b><br>For 3 Months &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; 30% &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 70%<br>For 6 Months &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 25% &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 75%<br>For 12 Months &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 20% &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 80%<br>For 24 Months &amp; above &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 15% &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; 85%</font><br>&nbsp;&nbsp; &nbsp;<br></font><ul><li><font face="Arial" size="3">Encashment before three months: only actual deposit will be returned.</font></li><li><font face="Arial" size="3">Encashment after three months but before maturity: profit will be calculated at the maturity rate then profit for three months will be deducted and the rest amount will be returned.</font></li></ul>', 'http://www.pubalibangla.com/Mudaraba-Term-Deposit.asp'),
(28, '<font face="Arial" size="3"><font size="5" color="#ff0000"><b>Mudaraba Term Deposit Receipt (MTDR) Account:</b></font><br><br>Mudaraba Term Deposit Receipt (MTDR) Account offers the customers the opportunity to invest a fixed amount for a fixed period at a fixed rate of profit. The customers have the option to re - invest their funds both principal amount and profit amount on maturity or principal amount and the profit amount being paid into their Mudaraba SB or Al - Wadiah CD accounts.<br><br><font size="4" color="#000099"><b>Features and Benefits:</b></font><br></font><ul><li><font face="Arial" size="3">Money can grow with attractive profit rate and flexible tenure.</font></li><li><font face="Arial" size="3">Automatic renewal facility at maturity.</font></li><li><font face="Arial" size="3">Loan facility against MTDR Account to meet urgent financial needs.</font></li><li><font face="Arial" size="3">Premature closure facility at savings rate.</font></li><li><font face="Arial" size="3">The customer may avail loan facilities against Mudaraba MTDR Account as security.</font></li></ul><font face="Arial" size="3">&nbsp;&nbsp; &nbsp;<br><font size="4" color="#000099"><b>Eligibility:</b></font><br></font><ul><li><font face="Arial" size="3">MTDR Account can be opened both for Individual and Corporate bodies.</font></li><li><font face="Arial" size="3">Only Resident Bangladeshi National is allowed to open Personal MTDR Account.</font></li><li><font face="Arial" size="3">Age bar for opening of Personal MTDR Account: 18 years or above.</font></li><li><font face="Arial" size="3">Joint account can be opened.</font></li><li><font face="Arial" size="3">Minor account can be opened under the supervision of his / her / their guardian.</font></li><li><font face="Arial" size="3">MTDR Account can be issued for senior citizen with attractive rate.</font></li></ul><font face="Arial" size="3">&nbsp;&nbsp; &nbsp;<br><font size="4" color="#000099"><b>Requirements:</b></font><br><br><font size="4" color="#330099"><b>Personal Banking:</b></font><br></font><ul><li><font face="Arial" size="3">Two copies of passport size photograph;</font></li><li><font face="Arial" size="3">Cheque on Self or Cash deposit;</font></li><li><font face="Arial" size="3">Photocopy of Citizenship Certificate / Passport / Voter I.D. Card / National I.D. Card / Driving License.</font></li><li><font face="Arial" size="3">Photograph of Nominee. In case of minor nominee, copy of the birth certificate and photograph required;</font></li><li><font face="Arial" size="3">Personal Information Form;</font></li><li><font face="Arial" size="3">KYC Form</font></li></ul><font face="Arial" size="3"><br></font>', 'https://www.southeastbank.com.bd/mudaraba_mtdr.php'),
(32, '<font face="Arial" size="3"><font size="5" color="#ff0000"><b>Deposit Products:</b></font><br><b>Islamic Banking Branches and Windows collect deposit in two basic principles:</b><br><br><i>1. Al-Wadiah Account.<br>2. Mudaraba Account.</i><br><br><font size="4" color="#000099"><b>A. Al-Wadiah Account:</b></font><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Al-Wadiah is the contract of giving total security of money of depositors. In this regard depositors will not participate or take any risk of business with bank. Depositors hold the right to withdraw his or her money at any banking time. Depositors do not get profit from bank. Bank can deduct service charge from the account for maintaining and securing the deposit.<br><br><font size="4" color="#000099"><b>B. Mudaraba Account:</b></font><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Mudaraba is a share base business system. Two party involve in the contract where one party invest all the capital (owner of capital or Shahib-Al-Mal) and another one use his or her knowledge, experience, ability, skill and labor to run the business ( Manager of capital or Mudarib).<br><br><b><font size="5" color="#ff0000">Mudaraba term deposit account:&nbsp;</font></b> &nbsp;<br><br><font color="#333399"><b>Deposit &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Weightage 2013 &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Provisional Profit 2013</b><br>(A) Upto 3 Months &nbsp;&nbsp; &nbsp;- &nbsp;&nbsp; &nbsp;-<br>(B) 3 Months and above but less than 6 Months. &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 0.93 &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10.50%<br>(C) 6 Months and above but less than 1 Year. &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 0.97 &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 11.00%<br>(D) 1 Year and above but less than 3 Year. &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1.01 &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 11.50%<br>(E) 2 Year and above but less than 3 Year. &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1.05 &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; 12.00%<br>(F) 3 Year and above &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1.09 &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; 12.50%</font></font>', 'http://www.agranibank.org/Deposit_Products.php');
